// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity ^0.8.0;

/**
 * @dev Standard NftAuction Errors
 * @dev NFT
 */
interface INftAuctionErrors {
    /**
     * @dev Indicates an error when the auction is not exist.
     * @dev 
     */
    error AuctionNotExist();

    /**
     * @dev Indicates an error when array lengths don't match in batch operations.
     * @dev 
     * @param tokenAddressesLength Length of the token addresses array.
     * @param priceFeedAddressesLength Length of the price feed addresses array.
     */
	error ArrayLengthMismatch(uint256 tokenAddressesLength, uint256 priceFeedAddressesLength);

    /**
     * @dev Indicates an error when no price feed is configured for a token.
     * @dev 
     * @param tokenAddress Address of the token that lacks price feed support.
     */
	error NoSupportedPriceFeed(address tokenAddress);

    /**
     * @dev Indicates an error when bid price is not high enough to win the auction.
     * @dev 
     * @param bidPrice The current bid price offered.
     * @param highestPrice The current highest bid price that needs to be exceeded.
     */
	error BidPriceNoHighEnough(uint256 bidPrice, uint256 highestPrice);

    // 

}

contracts/NftAuction/NftAuction.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import {AggregatorV3Interface} from "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";
import {INftAuctionErrors} from "./INftAuctionErrors.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";


/**
 * @title NftAuction
 * @dev NFTNftAuctionFactory
 */
contract NftAuction is
    ERC721Holder,
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    INftAuctionErrors
{
    //
    struct Auction {
        uint256 auctionId;
        //
        address seller;
        // 
        uint256 duration;
        // 
        uint256 startTime;
        //
        bool ended;
        // 
        address hightestBidder;
        // 
        uint256 highestBid;
        // NFT
        address nftContract;
        // NFT ID
        uint256 tokenId;
        //  0x etherc20
        // 0x0000000000000000000000000000000000000000 eth
        address tokenAddress;
        // 
        uint256 tokenCount;
    }

    // ============  ============

    /// @dev 
    Auction public auction;

    /// @dev =>
    mapping(address => AggregatorV3Interface) public dataFeeds;

    // ============  ============

    event BidPlaced(
        uint256 indexed auctionId,
        address tokenAddress,
        uint256 amount,
        address indexed higherBidder,
        uint256 highestPrice
    );

    event AuctionEnded(
        uint256 indexed auctionId,
        address indexed winner,
        uint256 finalPrice,
        bool hasWinner
    );

    // ============  ============

    /**
     * @dev 
     */
    function initialize(address _owner) public initializer {
        __ReentrancyGuard_init();
        __Ownable_init(_owner);
    }

    // ============  ============

    /**
     * @dev 
     * @param _auctionId ID
     * @param _duration 
     * @param _startPrice USD8
     * @param _nftAddress NFT
     * @param _tokenId NFT Token ID
     * @param _seller 
     */
    function createAuction(
        uint256 _auctionId,
        uint256 _duration,
        uint256 _startPrice,
        address _nftAddress,
        uint256 _tokenId,
        address _seller
    ) external onlyOwner {
        // 
        // require(_duration >= 60, "Duration must be at least 60 seconds"); // 1
        // require(_duration <= 30 days, "Duration cannot exceed 30 days"); // 30
        require(_startPrice > 0, "Start price must be greater than 0");
        require(_nftAddress != address(0), "Invalid NFT contract address");
        require(_seller != address(0), "Invalid seller address");


        // NFT
        require(
            IERC721(_nftAddress).ownerOf(_tokenId) == owner(),
            "NFT not owned by factory"
        );

        // 
        auction = Auction({
            auctionId: _auctionId,
            seller: _seller,
            duration: _duration,
            ended: false,
            hightestBidder: address(0),
            highestBid: _startPrice,
            startTime: block.timestamp,
            nftContract: _nftAddress,
            tokenId: _tokenId,
            tokenAddress: address(0), // ETH
            tokenCount: 0
        });
    }

    /**
     * @dev 
     * @param _auctionID ID
     * @param _tokenAddress address(0)ETH
     * @param _amount 
     */
    function placeBid(
        uint256 _auctionID,
        address _tokenAddress,
        uint256 _amount
    ) external payable nonReentrant {
        if (auction.startTime == 0) {
            revert("Auction does not exist");
        }

        // 
        if (
            auction.ended ||
            auction.startTime + auction.duration <= block.timestamp
        ) {
            revert("Auction has already ended");
        }

        // 
        require(
            msg.sender != auction.seller,
            "Seller cannot bid on own auction"
        );

        // ETHERC20
        if (_tokenAddress == address(0)) {
            require(msg.value == _amount, "ETH amount mismatch");
            require(_amount > 0, "ETH amount must be greater than 0");
        } else {
            require(msg.value == 0, "Should not send ETH for ERC20 bid");
            require(_amount > 0, "Token amount must be greater than 0");
        }

        // USD
        uint256 currentBid = getPriceBaseUSD(_tokenAddress, _amount);
        uint256 highestPrice = auction.highestBid; // USD

        // 
        if (currentBid <= highestPrice) {
            revert("Bid price is not high enough");
        }

        // 
        _refundPreviousBidder();

        // 
        if (_tokenAddress != address(0)) {
            IERC20(_tokenAddress).transferFrom(
                msg.sender,
                address(this),
                _amount
            );
        }
        // ETHmsg.value

        // 
        auction.highestBid = currentBid;
        auction.hightestBidder = msg.sender;
        auction.tokenAddress = _tokenAddress;
        auction.tokenCount = _amount;
        // 
        emit BidPlaced(
            _auctionID,
            _tokenAddress,
            _amount,
            auction.hightestBidder,
            auction.highestBid
        );
    }

    // ============  ============

    /**
     * @dev 
     */
    function _refundPreviousBidder() internal {
        if (auction.hightestBidder == address(0)) {
            return; // 
        }

        address previousBidder = auction.hightestBidder;
        address tokenAddress = auction.tokenAddress;
        uint256 amount = auction.tokenCount;

        if (tokenAddress == address(0)) {
            // ETH
            (bool success, ) = payable(previousBidder).call{value: amount}("");
            require(success, "ETH refund failed");
        } else {
            // ERC20
            IERC20(tokenAddress).transfer(previousBidder, amount);
        }
    }

    // ============  ============

    /**
     * @dev USD
     * @param tokenAddress address(0)ETH
     * @param amount 
     * @return price USD8
     */
    function getPriceBaseUSD(address tokenAddress, uint256 amount)
        public
        view
        returns (uint256)
    {
        return amount;
        // int256 pricePerToken = getChainlinkDataFeedLatestAnswer(tokenAddress);
        // if (pricePerToken <= 0) {
        // 	revert("Invalid price feed data");
        // }

        // // ( * ) / 10^8
        // // Chainlink8
        // uint256 totalValue = (uint256(pricePerToken) * amount) / 1e8;
        // return totalValue;
    }

    /**
     * @dev 
     * @param tokenAddresses 
     * @param priceFeedAddresses 
     */
    function setPriceFeed(
        address[] memory tokenAddresses,
        address[] memory priceFeedAddresses
    ) external onlyOwner {
        if (tokenAddresses.length != priceFeedAddresses.length) {
            revert("Array length mismatch");
        }
        for (uint256 i = 0; i < tokenAddresses.length; i++) {
            // require(tokenAddresses[i] != address(0), "Invalid token address");
            require(
                priceFeedAddresses[i] != address(0),
                "Invalid price feed address"
            );
            dataFeeds[tokenAddresses[i]] = AggregatorV3Interface(
                priceFeedAddresses[i]
            );
        }
    }

    /**
     * @dev Chainlink
     * @param tokenAddress 
     * @return 8
     */
    function getChainlinkDataFeedLatestAnswer(address tokenAddress)
        public
        view
        returns (int256)
    {
        AggregatorV3Interface dataFeed = dataFeeds[tokenAddress];

        // 
        if (address(dataFeed) == address(0)) {
            revert("No supported price feed for this token");
        }

        (
            ,
            /* uint80 roundId */
            int256 answer, /*uint256 startedAt*/ /*uint256 updatedAt*/ /*uint80 answeredInRound*/
            ,
            ,

        ) = dataFeed.latestRoundData();
        return answer;
    }

    /**
     * @dev NFT
     * @param _auctionID ID
     */
    function endAuction(uint256 _auctionID) external onlyOwner nonReentrant {
        // 
        if (auction.startTime == 0) {
            revert AuctionNotExist();
        }

        // 
        if (auction.ended) {
            revert("Auction has already ended");
        }

        // 
        if (auction.startTime + auction.duration > block.timestamp) {
            revert("Auction time has not ended yet");
        }

        bool hasWinner = auction.hightestBidder != address(0);
        address nftRecipient;

        if (hasWinner) {
            // NFT
            nftRecipient = auction.hightestBidder;

            // 
            if (auction.tokenAddress == address(0)) {
                // ETH
                (bool success, ) = payable(auction.seller).call{
                    value: auction.tokenCount
                }("");
                require(success, "Payment to seller failed");
            } else {
                // ERC20
                IERC20(auction.tokenAddress).transfer(
                    auction.seller,
                    auction.tokenCount
                );
            }
        } else {
            // NFT
            nftRecipient = auction.seller;
        }

        // NFT
        IERC721(auction.nftContract).safeTransferFrom(
            owner(),
            nftRecipient,
            auction.tokenId
        );

        // 
        auction.ended = true;

        // 
        emit AuctionEnded(
            _auctionID,
            hasWinner ? auction.hightestBidder : address(0),
            hasWinner ? auction.highestBid : 0,
            hasWinner
        );
    }

    // ============  ============

    /**
     * @dev 
     * @return auction 
     */
    function getAuctionInfo() external view returns (Auction memory) {
        return auction;
    }

    /**
     * @dev ID
     * @return auctionID ID
     */
    function getAuctionID() external view returns (uint256 auctionID) {
        return auction.auctionId;
    }

    /**
     * @dev 
     * @return 
     */
    function isAuctionEnded() external view returns (bool) {
        return
            auction.ended ||
            (auction.startTime + auction.duration <= block.timestamp);
    }

    /**
     * @dev 
     * @return 0
     */
    function getRemainingTime() external view returns (uint256) {
        if (auction.ended || auction.startTime == 0) {
            return 0;
        }

        uint256 endTime = auction.startTime + auction.duration;
        if (block.timestamp >= endTime) {
            return 0;
        }

        return endTime - block.timestamp;
    }

    /**
     * @dev ETH
     */
    receive() external payable {
        // ETH
    }
}

